import React from "react";
import { Route } from "react-router-dom";
import { firestore, fetchShopData } from "../../utils/firebase/firebase.utils";
import { connect } from "react-redux";

import CollectionPreview from "../../components/collection-preview/collection-preview.component";
import Category from "../../components/category/category.component";

import { updateShopData } from "../../redux/shop/shop.actions";

import "./shoppage.styles.scss";

class Shop extends React.Component {
  unsubscribeFromSnapshot = null;

  componentDidMount() {
    const { shopDataUpdater } = this.props;

    const shopDataRef = firestore.collection("shopData");

    shopDataRef.onSnapshot(async (snapshot) => {
      const shopData = fetchShopData(snapshot);

      shopDataUpdater(shopData);
    });
  }

  render() {
    const { match } = this.props;
    return (
      <div className="shop-page">
        <Route exact path={`${match.path}/`} component={CollectionPreview} />
        <Route path={`${match.path}/:categoryId`} component={Category}></Route>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  shopDataUpdater: (shopData) => dispatch(updateShopData(shopData)),
});

export default connect(null, mapDispatchToProps)(Shop);
