import { takeLatest, put, call, all } from "redux-saga/effects";
import userTypes from "./user.types";

import { auth, googleProvider } from "../../utils/firebase/firebase.utils";
import { googleSignInFailure, googleSignInSuccess } from "./users.actions";

export function* googleSignInStart() {
  try {
    const userRef = yield auth.signInWithPopup(googleProvider);
    console.log(userRef);
  } catch (error) {
    put(googleSignInFailure(error));
  }
}

export function* isGoogleSignIn() {
  yield takeLatest(userTypes.GOOGLE_SIGN_IN_START, googleSignInStart);
}

export function* userSagas() {
  yield all([call(isGoogleSignIn)]);
}
