import { createSelector } from "reselect";

const mapUrlStringToId = {
  hats: 1,
  sneakers: 2,
  jackets: 3,
  mens: 4,
  womens: 5,
};

const selectShop = (state) => state.shop;

export const selectShopItems = createSelector(
  [selectShop],
  (shop) => shop.shop_data
);

export const selectCategory = (categoryUrlParams) =>
  createSelector([selectShop], (shop) =>
    shop.shop_data.find((category) => category.id === categoryUrlParams)
  );
