export const addNewCartItem = (cartItems, newCartItem) => {
  const existingCartItem = cartItems.find(
    (cartItem) => cartItem.id === newCartItem.id
  );

  if (existingCartItem) {
    return cartItems.map((cartItem) =>
      cartItem.id === newCartItem.id
        ? { ...cartItem, quantity: cartItem.quantity + 1 }
        : cartItem
    );
  }

  return [...cartItems, { ...newCartItem, quantity: 1 }];
};

export const reduceCartItemQuantity = (cartItemsArr, cartItem) => {
  const item = cartItemsArr.find((el) => el.id === cartItem.id);

  if (item.quantity === 1) {
    cartItemsArr.splice(
      cartItemsArr.findIndex((el) => el.id === cartItem.id),
      1
    );

    return cartItemsArr;
  } else {
    return cartItemsArr.map((el) => {
      if (el.id === cartItem.id) el.quantity -= 1;
      return el;
    });
  }
};
