import userTypes from "./user.types";

export const setCurrentUser = (user) => {
  return {
    type: userTypes.SET_CURRENT_USER,
    payload: user,
  };
};

export const googleSignInStart = () => ({
  type: userTypes.GOOGLE_SIGN_IN_START,
});

export const googleSignInSuccess = (user) => ({
  type: userTypes.GOOGLE_SIGN_IN_SUCCESS,
  payload: user,
});

export const googleSignInFailure = (error) => ({
  type: userTypes.GOOGLE_SIGN_IN_FAILURE,
  payload: error,
});

export const emailSignInStart = (user) => ({
  type: userTypes.EMAIL_SIGN_IN_START,
  payload: user,
});

export const emailSignInSuccess = (user) => ({
  type: userTypes.EMAIL_SIGN_IN_SUCCESS,
  payload: user,
});

export const emailSignInFailure = (error) => ({
  type: userTypes.EMAIL_SIGN_IN_FAILURE,
  payload: error,
});

export const userLogoutStart = () => ({
  type: userTypes.USER_LOGOUT_START,
});

export const userLogoutSuccess = () => ({
  type: userTypes.USER_LOGOUT_SUCCESS,
});

export const userLogoutFailure = (error) => ({
  type: userTypes.USER_LOGOUT_FAILURE,
  payload: error,
});

export default setCurrentUser;
