import { takeLatest, put, call, all, takeEvery } from "redux-saga/effects";
import userTypes from "./user.types";

import {
  auth,
  googleProvider,
  createUserDocDB,
} from "../../utils/firebase/firebase.utils";
import {
  googleSignInFailure,
  googleSignInSuccess,
  userLogoutSuccess,
  userLogoutFailure,
} from "./users.actions";

export function* googleSignInStart() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);

    const userDocRef = yield call(createUserDocDB, user);
    const userSnapShot = yield userDocRef.get();

    console.warn(userSnapShot);

    yield put(googleSignInSuccess(userSnapShot));
  } catch (error) {
    yield put(googleSignInFailure(error));
  }
}

export function* logout() {
  try {
    const userRef = yield auth.signOut();

    console.log(userRef);

    yield put(userLogoutSuccess());
  } catch (error) {
    yield put(userLogoutFailure(error));
  }
}

export function* userLogoutAsync() {
  yield takeEvery(userTypes.USER_LOGOUT_START, logout);
}

export function* isGoogleSignIn() {
  yield takeLatest(userTypes.GOOGLE_SIGN_IN_START, googleSignInStart);
}

export function* userSagas() {
  yield all([call(isGoogleSignIn), call(userLogoutAsync)]);
}
