import { createSelector } from "reselect";
import memoize from "lodash.memoize";

const mapUrlStringToId = {
  hats: 1,
  sneakers: 2,
  jackets: 3,
  mens: 4,
  womens: 5,
};

const selectShop = (state) => state.shop;

export const selectShopItems = createSelector(
  [selectShop],
  (shop) => shop.shop_data
);

export const selectCategory = memoize((categoryUrlParams) =>
  createSelector([selectShopItems], (shop_data) =>
    shop_data.find(
      (category) => category.id === mapUrlStringToId[categoryUrlParams]
    )
  )
);
